enum Floor {
    Up,
    Down
}

fn floors(s: &str) -> Vec<Floor> {
    s.chars().map(|char| {
        match char {
            '(' => Floor::Up,
            ')' => Floor::Down,
            _ => panic!("invalid char")
        }
    }).collect()
}

fn floor_imperative(fs: &Vec<Floor>) -> i32 {
    let mut floor: i32 = 0;
    for f in fs.iter() {
        match f {
            Floor::Up => floor += 1,
            Floor::Down => floor -= 1,
        }
    }
    floor
}


fn floor_map(fs: &Vec<Floor>) -> i32 {
    fs.iter().map(|char| {
        match char {
            Floor::Up => 1,
            Floor::Down => -1
        }
    }).sum()
}

fn floor_fold(fs: &Vec<Floor>) -> i32 {
    fs.iter().fold(0, |mut acc, char| {
        match char {
            Floor::Up => acc += 1,
            Floor::Down => acc -= 1
        }
        acc
    })
}

fn basement_imperative(fs: &Vec<Floor>) -> Option<usize> {
    let mut floor: i32 = 0;
    for (i, c) in fs.iter().enumerate() {
        match c {
            Floor::Up => floor += 1,
            Floor::Down => floor -= 1
        }
        if floor < 0 {
            return Some(i+1)
        }
    }
    return None
}

